mapper.update:
update review 
   set review_no = #{review_no},
       review_name = #{review_name}, 
       review_email = #{review_email}, 
       review_title = #{review_title}, 
       review_rating = #{review_rating}
       review_content = #{review_content}
       review_rating = #{review_rating}
       member_id = #{member_id}
       product_no = #{product_no}
 where review_no = #{review_no}
